import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";



export default function Pokemones () {

  const [poke, setPoke] = useState([]);
  const [pokemonSelected, setPokemonSelected] = useState("");
  const navigate = useNavigate();
  const url = "https://pokeapi.co/api/v2/pokemon/";
  const getFotosPoke = async () => {
    const res = await fetch(url);
    const {results} = await res.json();
     setPoke(results);
    };
   const goToPokemonDetail = async () => {
    if (pokemonSelected) navigate(`/pokemones/${setPokemonSelected} `);
    else alert ("Debe seleccionar un pokemon");
   };
   
      useEffect(() => {
    getFotosPoke();
  }, []);


  return (
    <div>
      <h1>Bienvenido maestro pokem√≥n</h1>
      <div>
        <select
        value={pokemonSelected}
        className="form-select text-center"
        onChange={({target}) => setPokemonSelected(target.value)}>
          <option value="" disable>
            Pokemones
          </option>
          {poke.map(({name}, i) => (
            <option key={i} value={name}>
              {name}
            </option>
          ))}
        </select>
        <br/>
        <button on type="submit" value="ver detalles" />
      </div>
    </div>
  );
};
