import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";


export default function Pokemones () {
  
    const [poke,setPoke] = useState({});
    const baseUrl = "https://pokeapi.co/api/v2/pokemon/?limit=10";
    const { name, url } = useParams();

    const getDatosPersonaje = async(name) => {
        console.log(`${baseUrl}/${name}`);
        const res = await fetch(`${baseUrl}/${name}`);
        const data = await res.json();

        console.log(data.results);
        //Asignaci�n de los datos al estado personaje
        setPoke(data.results);
    }

    useEffect(() => {
        /* CallBack */
        getDatosPersonaje(name);
        /* Descarga del componente 
        return () => {
            cleanup
        };
        */
    }, /*Dependencias*/[name]);

  return (
    <div>
      <h1>Bienvenido maestro pokemón</h1>
      {data.rmap((item) => (
        <h4 key={item.id}>
          <Link to={`/blog/${item.id}`}>
            {item.id} - {item.title}
          </Link>
        </h4>
      ))}
    </div>
  );
};
