import { Card } from "react-bootstrap";

export default function Cards({ pokemos }) {

  const { name, stats, src, types } = pokemos;

  return (
    <Card style={{ width: "34rem" }} className="col-lg-3 col-md-6 col-sm-12">
      <Card.Img height="300" variant="top" src={src} />
      <Card.Body>
        <Card.Title className="fw-bold">{name}</Card.Title>
        <ul className="text-left">
          <Card.Text>
            {stats.map((stat, i) => (
              <li key={i}>
                {stat.name}: {stat.base}
              </li>
            ))}
          </Card.Text>
        </ul>
        <Card.Text className="text-secondary">{types}</Card.Text>
      </Card.Body>
    </Card>
  );
}
